const gulp = require('gulp');
const fileInclude = require('gulp-file-include');

// Task để concatenate và chèn file HTML
gulp.task('concatenate-html', function () {
  return gulp.src('src/*.html') // Đường dẫn đến các file HTML cần gộp
    .pipe(fileInclude({
      prefix: '@@',
      basepath: '@file'
    }))
    .pipe(gulp.dest('dist')); // Thư mục đích để lưu file gộp
});

// Task mặc định
gulp.task('default', gulp.series('concatenate-html'));
